mapper.insert:
INSERT INTO T_EDU_USER (
	   USER_NO
	 , EMP_SERIAL
	 , USER_NM
	 , BIRTH_DT
	 , GENDER_YN
	 , HIREDATE
	 , INPUT_DT
	 , JOB_NO
	 , DEPT_NO
	 , LINE_NO
	 , PHONE_NO
	 , SECOND_NO
	 , POST
	 , ADDRESS
	 , EMAIL
	 , MANAGE_YN
	 , ADMIN_YN
	 , PROFIL_PICTURE
) VALUES (
	   SEQ_EDU_USER.NEXTVAL
	 , (SELECT TO_CHAR(SYSDATE,'YYMM')||'-'||LPAD((NVL(MAX(SUBSTR(EMP_SERIAL, 6, 3)),0) + 1),3,'0')
	      FROM T_EDU_USER
	     WHERE HIREDATE BETWEEN TRUNC(SYSDATE,'MONTH') AND LAST_DAY(SYSDATE))
	 , #{user_nm}
	 , #{birth_dt}
	 , #{gender_yn}
	 , #{hiredate}
	 , sysdate
	 , #{job_no}
	 , #{dept_no}
	 , #{line_no}
	 , #{phone_no}
	 , #{second_no}
	 , #{post}
	 , #{address}
	 , #{email}
	 , #{manage_yn}
	 , #{admin_yn}
	 , #{profil_picture}
)

mapper.update:
UPDATE T_EDU_USER SET
	   DEPT_NO = #{dept_no}
	 , JOB_NO = #{job_no}
	 , LINE_NO = #{line_no}
	 , PHONE_NO = #{phone_no}
	 , SECOND_NO = #{second_no}
	 , POST = #{post}
	 , ADDRESS = #{address}
	 , EMAIL = #{email}

	 , ADMIN_YN = #{admin_yn}
	 , MANAGE_YN = #{manage_yn}
	 , TIME_STAMP = SYSDATE
 WHERE USER_NO = #{user_no}
