mapper.select:

	SELECT NVL(MAX(LEV_NO), 0) + 1
	  FROM T_EDU_CATEGORY
	 WHERE REF_NO = #{ref_no}


	SELECT COUNT(*) AS CNT
	  FROM T_EDU_CATEGORY
	 WHERE CATEGORY_NM = #{category_nm}
	   AND DEPT_NO = #{dept_no}

mapper.insert:

	INSERT INTO T_EDU_CATEGORY (
	       CATEGORY_NO
	     , REF_NO
	     , LEV_NO
	     , CATEGORY_NM
	     , DEPT_NO
	) VALUES (
		   SEQ_EDU_CATEGORY.NEXTVAL
		 , #{ref_no}
		 , #{lev_no}
		 , #{category_nm}
		 , #{dept_no}
	)
